import React, { Component } from 'react';

export default class Table extends Component {
    constructor(props) {
        super(props);
        this.state = {
            detail: '',
            itemList: [],
        };
    }

onInputChange = e => {
    this.setState({[e.target.name]: e.target.value});
}

addItem = e => {
    const { detail, itemList } = this.state;
    const arr = `${detail}`.replace(/'/ig, "").split("-");
    const itemName = arr[0];
    const itemPrice = arr[1];
    itemList.push({
        name: itemName,
        price: itemPrice,
        quantity: 5
    });

    this.setState({
        itemList,
        detail: ''
    });
}
increaseItem = index =>  {
    const { itemList } = this.state;
    itemList.quantity ++;
    this.setState({ itemList }) 
}
deleteItem = index => {
    const { itemList } = this.state;
    itemList.splice(index, 1);
    this.setState({ itemList });
};

    render() {
        const { detail, itemList } = this.state;
        const itemMarkUp = itemList.map((item, index)=>{
            return (
                <li key={`item-${index}`}>
                    {item.name} :: {item.quantity} :: {item.price}
                    <button onClick={(e) => this.increaseItem(index)}>+</button>

                    <input type="button" value="delete item"
                        onClick={(e) => this.deleteItem(index)} />
                </li>
            );
        });

        return (

            <div>
                <h1>My Cart</h1>
                    <form>
                        <div className="col-sm-10">
                            <input name="detail" className="form-control" value={detail} type="text" onChange={(e) => this.onInputChange(e)} />
                            <input type="button" value="Add Item" onClick={(e) => this.addItem(e)} disabled={!detail} />
                        </div>
                        <ul>
                            {itemMarkUp}
                        </ul>
                    </form>
            </div>

    );
    }
}
