import React, { Component } from 'react';

export default class Table extends Component {
    constructor(props) {
        super(props);
        this.state = {
            detail: '',
            itemList: [],
        };
    }

onInputChange = e => {
    this.setState({[e.target.name]: e.target.value});
}

addItem = e => {
    const { detail, itemList } = this.state;
    const arr = `${detail}`.replace(/'/ig, "").split("-");
    const itemName = arr[0];
    const itemPrice = arr[1];
    itemList.push({
        name: itemName,
        price: itemPrice,
        quantity: 5
    });

    this.setState({
        itemList,
        detail: ''
    });
}

    renderTags =() => {
        if(this.state.tags.length === 0)
            return <p>Please enter input!</p>;
        return   <table> {this.state.tags.map(tag => <tr>
                    <th>{`${this.props.item}`}</th>
                    <th>ID</th>
                    <th>Product</th>
                    <th>Quantity</th>
                    <th>Price</th>
                    <th>Action</th>
                </tr>
            )} </table>
    }


    render() {
        const { detail, itemList } = this.state;
        console.log('itemList:: ', itemList);
        const itemMarkUp = itemList.map((item, index)=>{
            return (
                <li key={`item-${index}`}>
                    {item.name} :: {item.quantity} :: {item.price}
                </li>
            );
        });

        return (

            <div>
                <h1>My Cart</h1>
                    <form>
                        <div className="col-sm-10">
                            <input name="detail" className="form-control" value={detail}
                               type="text" onChange={(e) => this.onInputChange(e)} />
                            <input type="button" value="Add Item"  
                                onClick={(e) => this.addItem(e)} disabled={!detail} />
                        </div>
                        <ul>
                            {itemMarkUp}
                        </ul>
                    </form>
            </div>

    );
    }
}
